void spfa() {
	queue<int> q;
	q.push(s);
	dist[s] = 0;
	while (!q.empty()) {
		int u=q.front(); q.pop();
		vis[u] = 0;
		for (int i=0; i<g[u].size(); i++) {
			int v=g[u][i].to, w=g[u][i].w;
			if (dist[u] + w < dist[v]) {
				dist[v] = dist[u] + w;
				if (!vis[v]) {
					vis[v] = 1;
					q.push(v);
				}
			}
		}
	}
}

int main() {
	int mindst = 1000;
	int m = 23827, n = 41*159+1000;
	FILE *fin = fopen("12_interim.in","r");
	for (int i=1, u, v, w; i<=m; i++) {
		fscanf(fin, "%d%d%d", &u, &v, &w);
		g[u].push_back((Edge) {v,w});
	}
	for (int i:starts) {
		s = i;
		std::cout << s << ", ";
		memset(vis, 0, sizeof(vis));
		memset(dist, INF, sizeof(dist));
		spfa();
		if (dist[3335] != INF) {
			mindst = std::min(mindst, dist[3335]);
			std::cout << s << ", " << dist[3335] << endl;
		}
	}
	std::cout << "\n\n\n" << mindst;
	
}
